using System;\nusing System.IO;\n\npublic class EventLogger {\n    private readonly string _logFilePath;\n\n    public EventLogger(string processId) {\n        // Ensure each process writes to its own log file\n        _logFilePath = $"process_{processId}_events.log";\n    }\n\n    public void LogEvent(string message) {\n        var timestampedMessage = $"{DateTime.Now:yyyy-MM-dd HH:mm:ss} - {message}";\n        File.AppendAllText(_logFilePath, timestampedMessage + Environment.NewLine);\n    }\n}