public static class Helper {\n    public static LinkedListNode<T> NodeAt<T>(this LinkedList<T> l, int index) {\n        LinkedListNode<T> x = l.First; // Start at the first node\n\n        while ((index--) > 0) { // Decrements index as long as it's positive\n            x = x.Next; // Move to the next node in the list\n            Console.Write(x.Value); // This print and delay are for illustration only, not necessary\n            Thread.Sleep(10000);     // Delays the loop for 10 seconds (for demonstration)\n        }\n\n        return x; // Return the node at the specified index\n    }\n}