public ActionResult Index()\n{\n    var files = // Assume this is your DB call to fetch all records\n        db.Files.OrderBy(f => f.Group).ToList(); // Replace with actual database access code\n\n    var groupedFiles = files\n        .GroupBy(f => f.Group)\n        .Select(g => new { GroupName = g.Key, Files = g.ToList() })\n        .ToList();\n\n    return View(groupedFiles);\n}