public class Order\n{\n    public List<OrderItem> Items { get; private set; } = new List<OrderItem>();\n\n    public void AddItem(OrderItem item)\n    {\n        Items.Add(item);\n        // Optionally raise an event here to notify observers\n    }\n\n    // Other order-related logic...\n}\n\npublic class OrderItem\n{\n    public decimal Cost { get; set; }\n    // Other item-related properties...\n}\n\npublic class OrderSummary\n{\n    public decimal MaxCost { get; private set; } = 0;\n\n    public void UpdateMaxCost(Order order)\n    {\n        MaxCost = order.Items.Max(item => item.Cost);\n    }\n\n    // Optionally subscribe to events from Order here...\n}