using System;\nusing System.Reflection;\n\nclass Program\n{\n    static void Main()\n    {\n        // Get the current method using reflection\n        MethodInfo methodInfo = typeof(Program).GetMethod(nameof(CalculateChecksum));\n\n        if (methodInfo != null)\n        {\n            var methodBody = methodInfo.GetMethodBody();\n            byte[] ilBytes = methodBody.GetILAsByteArray();\n\n            Console.WriteLine("MSIL bytes:");\n            foreach (var b in ilBytes)\n            {\n                Console.Write($"{b:X2} ");\n            }\n        }\n    }\n\n    static int CalculateChecksum()\n    {\n        // Example method for checksum calculation\n        return 42;\n    }\n}