using System;\nusing System.Data.SqlClient;\n\n// Assume 'localDateTime' is the DateTime value retrieved from your application code.\nDateTime localDateTime = DateTime.Now; // Example: This would be your user's local time.\n\n// Convert DateTime to UTC\nDateTime utcDateTime = localDateTime.ToUniversalTime();\n\n// Use parameterized queries to safely insert into SQL Server\nusing (SqlConnection connection = new SqlConnection("your_connection_string"))\n{\n    using (SqlCommand command = new SqlCommand("INSERT INTO YourTable (YourDateTimeColumn) VALUES (@UtcDateTime)", connection))\n    {\n        // Add the UTC DateTime as a parameter\n        command.Parameters.AddWithValue("@UtcDateTime", utcDateTime);\n\n        connection.Open();\n        command.ExecuteNonQuery();\n    }\n}