<%@ Page Language="C#" Inherits="System.Web.Mvc.ViewPage<Recipe>" %>\n<script src="<%= Url.Content("~/Scripts/MicrosoftAjax.js") %>"></script>\n<script src="<%= Url.Content("~/Scripts/MicrosoftMvcAjax.js") %>"></script>\n\n<div id="ingredientlistdiv">\n    <% Html.RenderPartial("IngredientsListControl", Model.Ingredients); %}\n</div>\n\n<div id="ingrediententrydiv" align="left">\n    <% using (Ajax.BeginForm("UpdateStep2", "Recipes", new AjaxOptions { UpdateTargetId = "ingredientlistdiv" }))\n       { %>\n        <p>\n            <label for="Units">Units:</label><br />\n            <%= Html.TextBox("Units", 0) %>\n            <%= Html.ValidationMessage("Units", "*") %>\n        </p>\n        <p>\n            <label for="Measure">Measure:</label><br />\n            <%= Html.TextBox("Measure") %>\n            <%= Html.ValidationMessage("Measure", "*") %>\n        </p>\n        <p>\n            <label for="IngredientName">Ingredient Name:</label><br />\n            <%= Html.TextBox("IngredientName") %>\n            <%= Html.ValidationMessage("IngredientName", "*") %>\n        </p>\n        <p><input type="submit" value="Save Ingredient" /></p>\n        <%= Html.Hidden("RecipeID", Model.RecipeID.ToString()) %>\n    <% } %>\n</div>