// Assuming you have created a DataSet named 'myDataSet', a TableAdapter named 'myTableAdapter',\n// and a BindingSource named 'myBindingSource'.\n\nprivate void LoadData()\n{\n    // Fill the DataTable in the DataSet using the TableAdapter\n    myTableAdapter.Fill(myDataSet.MyTableName);\n    \n    // Set the DataSource of the BindingSource to the DataTable within the DataSet\n    myBindingSource.DataSource = myDataSet.MyTableName;\n    \n    // Bind the DataGridView (assuming it's named dataGridView1) to the BindingSource\n    dataGridView1.DataSource = myBindingSource;\n}\n\nprivate void SaveData()\n{\n    // Send updates from the DataTable in the DataSet back to the database using the TableAdapter\n    myTableAdapter.Update(myDataSet.MyTableName);\n}