using System.Drawing; // For Bitmap and related classes\nusing ZXing;\n\npublic string DecodePdf417FromFile(string filePath)\n{\n    // Load the image containing the barcode\n    var bitmap = new Bitmap(filePath);\n    \n    // Create a barcode reader instance\n    var barcodeReader = new BarcodeReader();\n\n    // Decode the first found barcode\n    var result = barcodeReader.Decode(bitmap);\n\n    if (result != null)\n    {\n        return result.Text;\n    }\n\n    throw new InvalidOperationException("No PDF-417 barcode found in the image.");\n}