class Entity {\n    public EntityCollection Location { get; private set; }\n\n    public void MoveTo(EntityCollection to) {\n        if (Location != null && Location.Contains(this)) {\n            Location.Remove(this);\n        }\n        to.Add(this);\n        Location = to;\n    }\n}\n\n// Client code example:\nEntity Qux = ...; // Assume instantiated and possibly in Foo\nQux.MoveTo(Bar); // Moves from current collection, assumed to be Foo, to Bar