using System;\nusing System.IO;\n\nclass Program\n{\n    static void Main(string[] args)\n    {\n        string tempPath = Path.Combine(Environment.GetFolderPath(Environment.SpecialFolder.Windows), "Microsoft.NET", "Framework", "v2.0.50727", "Temporary ASP.NET Files");\n        try\n        {\n            Directory.Delete(tempPath, true);\n            Console.WriteLine("Temporary ASP.NET Files cleared successfully.");\n        }\n        catch (Exception ex)\n        {\n            Console.WriteLine($"Error: {ex.Message}");\n        }\n    }\n}