using DiffPlex;\nusing DiffPlex.DiffBuilder;\nusing DiffPlex.DiffBuilder.Model;\n\npublic string GenerateDiffHtml(string oldText, string newText)\n{\n    var inlineBuilder = new InlineDiffBuilder(new Differ());\n    var diffResult = inlineBuilder.BuildDiffModel(oldText, newText);\n\n    var htmlBuilder = new HtmlDiffToSideBySideHtmlTagHighlightFormatter();\n    return htmlBuilder.GetHtml(diffResult);\n}