using System;\nusing System.IO;\n\nclass Program\n{\n    static void Main()\n    {\n        string filePath = "yourfile.txt";\n\n        // Open the file with read and write access, allowing other processes to also read or write.\n        using (FileStream fs = new FileStream(filePath, FileMode.Create, FileAccess.ReadWrite, FileShare.ReadWrite))\n        {\n            // Perform operations on the file\n            using (StreamWriter sw = new StreamWriter(fs))\n            {\n                sw.WriteLine("Hello world!");\n            }\n\n            // Start process B\n            System.Diagnostics.Process.Start("msbuild.exe");\n\n            // Give process B some time to do its work if necessary\n\n            // Close your stream before trying to delete the file\n        }  // FileStream is closed here\n\n        // Attempt to delete the file after ensuring all processes have finished using it.\n        File.Delete(filePath);\n    }\n}