using System;\nusing System.Text.RegularExpressions;\n\nclass Program\n{\n    static void Main()\n    {\n        string[] inputStrings = { "Some Text here", "some text Here" };\n        string[] wordsToMatch = { "Some", "Text", "Here" };\n\n        foreach (var input in inputStrings)\n        {\n            string result = input;\n            foreach (var word in wordsToMatch)\n            {\n                // Use RegexOptions.IgnoreCase to make the search case-insensitive\n                string pattern = $@"\b{Regex.Escape(word)}\b";\n                MatchEvaluator evaluator = match => $"<dfn title=\"Definition of {match.Value}\">{match.Value}</dfn>";\n                result = Regex.Replace(result, pattern, evaluator, RegexOptions.IgnoreCase);\n            }\n            Console.WriteLine(result);\n        }\n    }\n}