using System;\nusing System.IO;\nusing System.Security.AccessControl;\n\nclass Program\n{\n    static void Main()\n    {\n        string path = @"C:\YourFlashDrive";\n        \n        // Ensure that the directory exists on your flash drive\n        Directory.CreateDirectory(path);\n        \n        var dirInfo = new DirectoryInfo(path);\n\n        // Create a new DirectorySecurity object.\n        DirectorySecurity ds = dirInfo.GetAccessControl(AccessControlSections.Audit);\n\n        // Add an audit rule for deletion events.\n        FileSystemAuditRule fsar = new FileSystemAuditRule(\n            "BUILTIN\\Users",\n            FileSystemRights.Delete | FileSystemRights.DeleteSubdirectoriesAndFiles,\n            InheritanceFlags.ContainerInherit | InheritanceFlags.ObjectInherit,\n            PropagationFlags.None,\n            AuditFlags.Success);\n\n        ds.AddAuditRule(fsar);\n        \n        // Apply the new access rules.\n        dirInfo.SetAccessControl(ds);\n    }\n}