using System;\nusing System.Diagnostics;\n\npublic class AttachToProcessExample\n{\n    public static void Main()\n    {\n        // Example: Find notepad.exe\n        Process[] processes = Process.GetProcessesByName("notepad");\n\n        if (processes.Length > 0)\n        {\n            Process process = processes[0]; // Get the first instance of notepad\n\n            Console.WriteLine($"Attached to {process.ProcessName}, ID: {process.Id}");\n\n            // You can now access various properties and methods of the Process class\n            Console.WriteLine("Main Window Title:" + process.MainWindowTitle);\n        }\n        else\n        {\n            Console.WriteLine("Process not found.");\n        }\n    }\n}