using System.Data;\n\npublic class Employee\n{\n    public List<DataTable> GetEmployees()\n    {\n        // Create your DataTables here\n        DataTable dataTable1 = new DataTable();\n        DataTable dataTable2 = new DataTable();\n        DataTable dataTable3 = new DataTable();\n\n        // Example: Adding some sample columns and rows for demonstration\n        dataTable1.Columns.Add("ID", typeof(int));\n        dataTable1.Rows.Add(1);\n        dataTable1.Rows.Add(2);\n\n        dataTable2.Columns.Add("Name", typeof(string));\n        dataTable2.Rows.Add("John Doe");\n        dataTable2.Rows.Add("Jane Smith");\n\n        dataTable3.Columns.Add("Age", typeof(int));\n        dataTable3.Rows.Add(30);\n        dataTable3.Rows.Add(25);\n\n        // Create a list to hold your DataTables\n        List<DataTable> dataTables = new List<DataTable>\n        {\n            dataTable1,\n            dataTable2,\n            dataTable3\n        };\n\n        return dataTables;\n    }\n}