protected void Page_Load(object sender, EventArgs e)\n{\n    Employee employeeInstance = new Employee();\n    List<DataTable> dataTables = employeeInstance.GetEmployees();\n\n    // Assign each DataTable to a specific GridView\n    gridView1.DataSource = dataTables[0];\n    gridView1.DataBind();\n\n    gridView2.DataSource = dataTables[1];\n    gridView2.DataBind();\n\n    gridView3.DataSource = dataTables[2];\n    gridView3.DataBind();\n}