using System.IO;\nusing MsgReader.Outlook;\nusing MsgReader;\n\npublic class MsgParser\n{\n    public Storage.Message ParseMsgFromMemoryStream(MemoryStream memoryStream)\n    {\n        // Create a new Storage.Message object and load it with the content of the MemoryStream\n        var message = new Storage.Message();\n        message.Load(memoryStream);\n        \n        return message;\n    }\n}