// Method to handle conversion from UI value to DB null\nobject ConvertToDbValue(object uiValue)\n{\n    // Define your sentinel values here, e.g., string.Empty or "<NONE>"\n    if (uiValue == null || uiValue.ToString().Equals("<NONE>", StringComparison.OrdinalIgnoreCase))\n    {\n        return DBNull.Value;\n    }\n    return uiValue;\n}\n\n// Example usage in your update logic\nvoid SaveToDatabase()\n{\n    // Convert UI value to a value suitable for database operations\n    object dbTechNumber = ConvertToDbValue(cboTechNumber.SelectedValue);\n    object dbAnotherField = ConvertToDbValue(anotherComboBox.SelectedValue);\n\n    taDevice.UpdateQuery_Restage(\n        lemsCredsID: Convert.ToInt32(dbTechNumber),\n        selectedSerial: selectedSerial,\n        anotherField: dbAnotherField // Pass other fields similarly\n    );\n}