using System;\nusing System.IO.Pipes;\nusing System.Text;\n\nclass Program {\n    static void CheckAndSendShowFormCommand() {\n        var processes = System.Diagnostics.Process.GetProcessesByName("MyApp");\n        \n        if (processes.Length > 1) { // There is another instance running\n            using (var client = new NamedPipeClientStream(".", "MyAppPipe", PipeDirection.Out)) {\n                client.Connect();\n\n                using (var writer = new StreamWriter(client)) {\n                    writer.WriteLine("ShowForm"); // Send command to show the form\n                    writer.Flush();\n                }\n            }\n\n            return; // Exit this instance\n        }\n\n        // If no other instances are running, proceed with normal startup logic\n    }\n\n    static void Main(string[] args) {\n        CheckAndSendShowFormCommand();\n\n        // Your main application code here...\n        Console.ReadLine(); // To keep the console open in this example\n    }\n}