// Using ArrayList (non-generic)\nArrayList arrayList = new ArrayList();\narrayList.Add(1);  // Box the int to an object\nint intValueFromArrayList = (int)arrayList[0];  // Unbox the object back to an int\n\n// Using List<object> (generic)\nList<object> list = new List<object>();\nlist.Add(1);  // No boxing needed\nint intValueFromList = (int)list[0];  // No unboxing needed, but explicit cast required