using System;\nusing System.Collections.Generic;\n\nclass Program\n{\n    static void Main()\n    {\n        var windowsVersions = new Dictionary<(int major, int minor), string>\n        {\n            { (6, 0), "Windows Vista" },\n            { (6, 1), "Windows 7" },\n            { (6, 2), "Windows 8" },\n            { (6, 3), "Windows 8.1" },\n            { (10, 0), "Windows 10" },\n            // Add more as needed\n        };\n\n        var version = Environment.OSVersion.Version;\n        if (windowsVersions.TryGetValue((version.Major, version.Minor), out string productName))\n        {\n            Console.WriteLine($"Product Name: {productName}");\n        }\n        else\n        {\n            Console.WriteLine("Unknown Windows version.");\n        }\n    }\n}