using System;\nusing System.Management;\n\nclass NetworkDriveLabelReader\n{\n    public static void GetNetworkDriveLabels()\n    {\n        string query = "SELECT * FROM Win32_MappedLogicalDisk";\n        \n        using (ManagementObjectSearcher searcher = new ManagementObjectSearcher(query))\n        {\n            foreach (ManagementObject drive in searcher.Get())\n            {\n                Console.WriteLine("Drive Name: {0}", drive["Name"]);\n                Console.WriteLine("Volume Name: {0}", drive["VolumeName"] ?? "No Label");\n                Console.WriteLine();\n            }\n        }\n    }\n\n    static void Main()\n    {\n        GetNetworkDriveLabels();\n    }\n}