using System;\nusing System.Diagnostics;\nusing System.Runtime.InteropServices;\n\nnamespace ManagedDllExample\n{\n    [ComVisible(true)]\n    [Guid("9BBA1F20-C59D-4CDE-B6A3-DBBCF8FFAEFD")] // Use a unique GUID for your component\n    [ClassInterface(ClassInterfaceType.None)]\n    public class ShellExecutor : IShellExecutor\n    {\n        public void ExecuteExternalExe(string path, string arguments)\n        {\n            ProcessStartInfo start = new ProcessStartInfo();\n            start.FileName = path;\n            start.Arguments = arguments;\n\n            try\n            {\n                using (Process proc = Process.Start(start))\n                {\n                    // Optionally wait or read output if needed\n                }\n            }\n            catch (Exception ex)\n            {\n                throw new Exception("Error executing process", ex);\n            }\n        }\n    }\n\n    [ComVisible(true)]\n    public interface IShellExecutor\n    {\n        void ExecuteExternalExe(string path, string arguments);\n    }\n}