// Ensure the account running your application pool has necessary rights\n   var directoryPath = @"\\NetworkShare\UploadDirectory";\n   if (!Directory.Exists(directoryPath))\n   {\n       try\n       {\n           Directory.CreateDirectory(directoryPath);\n       }\n       catch (Exception ex)\n       {\n           // Log exception for auditing and debugging\n           Console.WriteLine("Error creating directory: " + ex.Message);\n       }\n   }\n\n   // Set proper ACLs if required (be cautious with setting permissions)\n   var dirInfo = new DirectoryInfo(directoryPath);\n   var directorySecurity = dirInfo.GetAccessControl();\n   var domainAccount = new NTAccount(@"DOMAIN\username");\n   directorySecurity.AddAccessRule(new FileSystemAccessRule(domainAccount, FileSystemRights.Modify | FileSystemRights.Synchronize, InheritanceFlags.ContainerInherit | InheritanceFlags.ObjectInherit, PropagationFlags.None, AccessControlType.Allow));\n   dirInfo.SetAccessControl(directorySecurity);