public ActionResult GetPersonDetails(int personId)\n{\n    var person = db.Persons.Find(personId);\n    \n    if (person == null)\n    {\n        return HttpNotFound();\n    }\n\n    // Render your partial view to a string\n    string htmlStringPartialView = this.RenderPartialViewToString("_PersonDetailsPartial", person);\n\n    // Serialize your model to JSON\n    string jsonModel = JsonConvert.SerializeObject(person);\n\n    // Return both as part of a JSON object\n    return Json(new \n                { \n                    HtmlContent = htmlStringPartialView,\n                    ModelData = jsonModel \n                }, JsonRequestBehavior.AllowGet);\n}