class Program {\n    private Action _stepOne;\n    private Action _stepTwo;\n    private Action _stepThree = () => { /* Default implementation */ };\n\n    public Program(Action stepOne, Action stepTwo) {\n        _stepOne = stepOne ?? throw new ArgumentNullException(nameof(stepOne));\n        _stepTwo = stepTwo ?? throw new ArgumentNullException(nameof(stepTwo));\n    }\n\n    public void TemplateMethod() {\n        _stepOne();\n        _stepTwo();\n        _stepThree?.Invoke();  // Null-conditional invocation in case not provided\n    }\n}\n\nclass ProgramExample {\n    static void Main(string[] args) {\n        var program = new Program(\n            stepOne: () => Console.WriteLine("Step One"),\n            stepTwo: () => Console.WriteLine("Step Two")\n        );\n\n        program.TemplateMethod();\n    }\n}