using System.ComponentModel;\nusing System.Configuration.Install;\nusing System.ServiceProcess;\n\n[RunInstaller(true)]\npublic class MyServiceInstaller : Installer\n{\n    public override void Install(System.Collections.Hashtable stateSaver)\n    {\n        Installer serviceProcessInstaller = new ServiceProcessInstaller();\n        Installer serviceInstaller = new ServiceInstaller();\n\n        // Set the account type for the service process installer.\n        serviceProcessInstaller.Account = ServiceAccount.LocalSystem;\n\n        // Configure the service installer properties.\n        serviceInstaller.DisplayName = "My New Service";\n        serviceInstaller.ServiceName = "my_new_service";\n        serviceInstaller.StartType = ServiceStartMode.Manual;\n        this.Installers.Add(serviceProcessInstaller);\n        this.Installers.Add(serviceInstaller);\n\n        base.Install(stateSaver);\n    }\n}