public partial class MyWpfUserControl : UserControl\n{\n    public bool IsValid\n    {\n        get { return (bool)GetValue(IsValidProperty); }\n        set { SetValue(IsValidProperty, value); }\n    }\n\n    public static readonly DependencyProperty IsValidProperty =\n        DependencyProperty.Register("IsValid", typeof(bool), typeof(MyWpfUserControl),\n            new PropertyMetadata(true));\n\n    public MyWpfUserControl()\n    {\n        InitializeComponent();\n        txtSomething.AddHandler(Validation.ErrorEvent, new RoutedEventHandler((sender, args) =>\n        {\n            IsValid = !Validation.GetHasError(txtSomething);\n        }));\n    }\n}