using (OracleConnection conn = new OracleConnection(this.connStr))\n{\n    using (OracleCommand com = new OracleCommand("MYPKG.MYQUERY", conn))\n    {\n        OracleDataAdapter adapter = new OracleDataAdapter(com);\n        com.Parameters.Add("searchParam", OracleDbType.Varchar2).Value = "myName";\n        OracleParameter refCursorParameter = com.Parameters.Add("outCursor", OracleDbType.RefCursor, ParameterDirection.Output);\n\n        com.CommandType = CommandType.StoredProcedure;\n        conn.Open();\n        DataSet ds = new DataSet();\n        \n        try\n        {\n            adapter.Fill(ds);\n        }\n        finally\n        {\n            // Optionally handle ref cursor explicitly if needed\n            if (refCursorParameter.Value != null)\n            {\n                ((OracleRefCursor)refCursorParameter.Value).Close(); \n            }\n        }\n    } // OracleCommand is disposed here\n} // OracleConnection is closed and disposed here