using System;\nusing System.IO;\n\npublic class LogConsolidator {\n    public static void ConsolidateLogs(string outputFilePath) {\n        var directoryInfo = new DirectoryInfo(Directory.GetCurrentDirectory());\n        using (var writer = File.CreateText(outputFilePath)) {\n            foreach (var fileInfo in directoryInfo.GetFiles("process_*.log")) {\n                string[] fileLines = File.ReadAllLines(fileInfo.FullName);\n                foreach (string line in fileLines) {\n                    writer.WriteLine(line);\n                }\n            }\n        }\n    }\n}