protected void Page_Load(object sender, EventArgs e)\n{\n    if (!IsPostBack)\n    {\n        DataTable dt = GetDataFromDataSource();\n        \n        foreach (DataRow row in dt.Rows)\n        {\n            // Assuming FormatEventDescription is a method that formats the description based on the History data.\n            string formattedDesc = FormatEventDescription(row);\n            \n            // Store this formatted description in two fields; one for display and another for filter\n            row["EventDescriptionForDisplay"] = formattedDesc;\n            row["EventDescriptionForFilter"] = formattedDesc;\n        }\n\n        grdHistory.DataSource = dt;\n        grdHistory.DataBind();\n    }\n}