<%@ Master Language="C#" %>\n<!DOCTYPE html>\n<html xmlns="http://www.w3.org/1999/xhtml">\n<head runat="server">\n    <title>Master Page</title>\n</head>\n<body>\n<form id="form1" runat="server">\n  <asp:ScriptManager ID="ScriptManager1" runat="server"></asp:ScriptManager>\n  <div>\n    <a href="#" onclick="loadContent('PageA.aspx'); return false;">Load Page A</a><br />\n    <a href="#" onclick="loadContent('PageB.aspx'); return false;">Load Page B</a>\n    \n    <asp:UpdatePanel ID="MainContent" runat="server">\n      <ContentTemplate>\n        <asp:PlaceHolder id="ContentPlaceholder1" runat="server"></asp:PlaceHolder>\n      </ContentTemplate>\n    </asp:UpdatePanel>\n  </div>\n</form>\n<script type="text/javascript">\n  function loadContent(pageUrl) {\n    var pageLoader = new Sys.WebForms.PageRequestManager.getInstance();\n    if (pageLoader) {\n        $('#<%= ContentPlaceholder1.ClientID %>').load(pageUrl);\n    }\n  }\n</script>\n</body>\n</html>