// Create an instance of the typed DataTable.\nDataSetProducts.productsDataTable typedProductTable = new DataSetProducts.productsDataTable();\n\n// Import the rows from the query result into the typed DataTable.\ntypedProductTable.BeginLoadData();\nforeach (var item in productlist.Skip(begin).Take(pagesize))\n{\n    typedProductTable.ImportRow(item);\n}\ntypedProductTable.EndLoadData();\n\nreturn typedProductTable;