using System;\nusing Microsoft.SqlServer.Management.Smo;\n\nclass Program\n{\n    static void Main(string[] args)\n    {\n        // Replace "YourServerName" with your server name or connection string\n        ServerConnection connection = new ServerConnection("YourServerName");\n        \n        try\n        {\n            Server server = new Server(connection);\n\n            Console.WriteLine("SQL Server Edition: {0}", server.Edition);\n        }\n        catch (Exception ex)\n        {\n            Console.WriteLine("An error occurred: " + ex.Message);\n        }\n        finally\n        {\n            connection.Disconnect();\n        }\n    }\n}