using System;\nusing System.Messaging;\n\nclass Program\n{\n    static void Main()\n    {\n        string queuePath = @"FormatName:Direct=TCP:YourServerName\private$\emergency";\n        \n        if (!MessageQueue.Exists(queuePath))\n            MessageQueue.Create(queuePath);\n\n        using (MessageQueue queueSender = new MessageQueue(queuePath))\n        {\n            // Simulate sending an emergency message\n            Message msg = new Message("Emergency: Evacuate immediately!");\n            msg.Formatter = new XmlMessageFormatter(new Type[] { typeof(string) });\n            \n            queueSender.Send(msg);\n            Console.WriteLine("Message Sent!");\n        }\n    }\n}