protected void UploadButton_Click(object sender, EventArgs e)\n{\n    string folderPath = Server.MapPath("~/pics/");\n    \n    // Check if folder exists, else create it.\n    if (!Directory.Exists(folderPath))\n    {\n        Directory.CreateDirectory(folderPath);\n    }\n\n    // Check file extension and validate file size if necessary\n    string fileExtension = Path.GetExtension(FileUploadControl.FileName).ToLower();\n    if (FileUploadControl.HasFile && \n        (fileExtension == ".jpg" || fileExtension == ".jpeg" || fileExtension == ".png"))\n    {\n        // Save the file to the pics folder.\n        FileUploadControl.SaveAs(folderPath + Path.GetFileName(FileUploadControl.FileName));\n        lblMessage.Text = "File uploaded successfully.";\n    }\n    else\n    {\n        lblMessage.Text = "Please upload a .jpg or .jpeg or .png file only.";\n    }\n}