using System;\nusing System.IO;\nusing System.Security.Cryptography.X509Certificates;\n\nclass Program\n{\n    static void Main()\n    {\n        // Path to your certificate file and its password\n        string certPath = @"C:\path\to\yourcertificate.pfx";\n        string password = "YourCertificatePasswordHere"; // Replace with your password or use a secure way to handle it\n        \n        // Load the certificate from a .pfx file\n        X509Certificate2 certificate = new X509Certificate2(certPath, password, X509KeyStorageFlags.MachineKeySet);\n        \n        // Open the trusted people store on the local machine in write mode\n        using (X509Store store = new X509Store(StoreName.TrustedPeople, StoreLocation.LocalMachine))\n        {\n            store.Open(OpenFlags.ReadWrite);\n            \n            // Add the certificate to the store\n            store.Add(certificate);\n            \n            Console.WriteLine("Certificate installed successfully.");\n        }\n    }\n}