protected override void OnModelCreating(DbModelBuilder modelBuilder)\n{\n    // Configure Paragraphs\n    modelBuilder.Entity<Paragraph>()\n        .HasKey(p => p.ParagraphID);\n\n    // Configure ImageParagraphs (TPT inheritance)\n    modelBuilder.Entity<ImageParagraph>()\n        .HasKey(ip => ip.ParagraphID);\n\n    modelBuilder.Entity<ImageParagraph>()\n        .Map(mapped =>\n            mapped.MapInheritedProperties()\n                  ..ToTable("ImageParagraphs"));\n\n    // Configure LinkListParagraph differently, since TPT isn't possible here\n    modelBuilder.Entity<LinkListParagraph>()\n        .HasKey(llp => new { llp.ParagraphID, llp.LinkID });\n\n    modelBuilder.Entity<LinkListParagraph>()\n        .Map(mapped =>\n            mapped.Properties(p => new \n                           {\n                               p.Name,\n                               p.OrdinalNumber,\n                               p.HtmlContentFragment,\n                               p.LinkID\n                           })\n                  .ToTable("LinkListParagraphs"));\n}