using System;\nusing DocumentFormat.OpenXml.Packaging;\nusing System.IO;\n\nclass Program\n{\n    static void Main(string[] args)\n    {\n        using (WordprocessingDocument document = WordprocessingDocument.Open(@"C:\path\to\your\document.docx", false))\n        {\n            // Extract Images\n            foreach (PackagePart imagePart in document.Parts.SelectMany(p => p.GetPartsOfType<ImagePart>()))\n            {\n                using (Stream source = imagePart.GetStream FileMode.Open, FileAccess.Read), destination = new FileStream($@"C:\path\to\save\{imagePart.Uri.OriginalString.Split('/').Last()}", FileMode.Create))\n                {\n                    CopyStream(source, destination);\n                }\n            }\n\n            // Extracting Equations is not directly supported in Open XML SDK\n            // You might need to inspect the XML manually or use 3rd party libraries\n        }\n    }\n\n    private static void CopyStream(Stream source, Stream destination)\n    {\n        byte[] buffer = new byte[4096];\n        int read;\n        while ((read = source.Read(buffer, 0, buffer.Length)) > 0)\n        {\n            destination.Write(buffer, 0, read);\n        }\n    }\n}