using System;\nusing Microsoft.Office.Interop.Word;\nusing System.IO;\n\nclass Program\n{\n    static void Main(string[] args)\n    {\n        Application wordApp = new Application();\n        Document document = null;\n        try\n        {\n            object file = @"C:\path\to\your\document.doc";\n            document = wordApp.Documents.Open(ref file);\n\n            // Extract Images\n            int imageCount = 1;\n            foreach (InlineShape shape in document.InlineShapes)\n            {\n                if (shape.Type == Microsoft.Office.Interop.Word.WdInlineShapeType.wdInlineShapePicture)\n                {\n                    string fileName = $@"C:\path\to\save\image{imageCount}.png";\n                    shape.ConvertToShape.Export(fileName, PpShapeFormat.ppShapeFormatPNG);\n                    imageCount++;\n                }\n            }\n\n            // Extract Equations is more complex and may require additional libraries or manual extraction\n\n        }\n        catch (Exception e)\n        {\n            Console.WriteLine(e.Message);\n        }\n        finally\n        {\n            if (document != null) document.Close();\n            wordApp.Quit();\n        }\n    }\n}