using System.IO;\nusing OpenPop.Mime;\nusing OpenPop.Pop3;\n\npublic class EmlParser\n{\n    public Message ParseEmlFromMemoryStream(MemoryStream memoryStream)\n    {\n        // Convert MemoryStream to byte array\n        var messageBytes = memoryStream.ToArray();\n        \n        // Create a new Message object with the content of the byte array\n        Message message = new Message(messageBytes);\n        \n        return message;\n    }\n}