using System;\nusing System.Runtime.InteropServices;\n\nnamespace FancyApp {\n    class Program\n    {\n        // Assuming ConsoleApp.dll is in the same directory as FancyApp.exe\n        [DllImport("ConsoleApp.dll", EntryPoint = "DisplayHelloFromDLL", CallingConvention = CallingConvention.Cdecl)]\n        public static extern void DisplayHelloFromDLL();\n\n        static void Main()\n        {\n            Console.WriteLine("Hello from the World of 2008!");\n            try\n            {\n                DisplayHelloFromDLL();\n            }\n            catch (Exception ex)\n            {\n                Console.Error.WriteLine($"Error calling DLL: {ex.Message}");\n            }\n\n            Console.WriteLine("Press any key to exit");\n            Console.ReadKey();\n        }\n    }\n}