private void dataGridView1_SortCompare(object sender, DataGridViewSortCompareEventArgs e)\n{\n    // Assuming the column index you want to sort is 1 (adjust as necessary)\n    if (dataGridView1.Columns[e.Column.Index] == myCustomColumn)\n    {\n        e.SortResult = string.Compare(e.CellValue1.ToString(), e.CellValue2.ToString());\n        e.Handled = true;\n    }\n}\n\nprivate void dataGridView1_ColumnHeadersClick(object sender, DataGridViewCellMouseEventArgs e)\n{\n    // Check if the click is on the header of your custom column\n    if (dataGridView1.Columns[e.ColumnIndex] == myCustomColumn)\n    {\n        ListSortDirection direction = ListSortDirection.Ascending;\n        if (dataGridView1.SortOrder == SortOrder.Ascending)\n            direction = ListSortDirection.Descending;\n\n        dataGridView1.Sort((DataGridViewColumnSorter)(myCustomColumn.HeaderCell.SortGlyphDirection == SortOrder.Descending ? new DataGridViewColumnSorter(comparer) : comparer), direction);\n    }\n}\n\n// Add this line in your form constructor or load method to hook up the event\ndataGridView1.SortCompare += dataGridView1_SortCompare;