var query = from c in DataContext.Categories\n            join p in DataContext.Products on c.Id equals p.CategoryId into ps\n            from pc in ps.DefaultIfEmpty()\n            group pc by new { c.Name, c.DisplayName } into g\n            select new \n            {\n                Name = g.Key.Name,\n                DisplayName = g.Key.DisplayName,\n                ProductCount = g.Count(x => x != null)\n            };