using System;\nusing System.Data;\nusing System.Data.OracleClient;\n\nclass Program\n{\n    static void Main()\n    {\n        string connectionString = "your_connection_string_here"; // Replace with your actual connection string\n        string procedureName = "your_procedure_name_here"; // Replace with the name of your procedure or function\n\n        using (OracleConnection connection = new OracleConnection(connectionString))\n        {\n            connection.Open();\n\n            string query = @"\n                SELECT text \n                FROM all_source \n                WHERE owner = :owner AND name = :name\n                ORDER BY line";\n\n            using (OracleCommand command = new OracleCommand(query, connection))\n            {\n                // Adjust the schema(owner) if necessary, here it's assumed to be 'YOUR_SCHEMA'\n                command.Parameters.AddWithValue("owner", "YOUR_SCHEMA");\n                command.Parameters.AddWithValue("name", procedureName);\n\n                using (OracleDataReader reader = command.ExecuteReader())\n                {\n                    while (reader.Read())\n                    {\n                        Console.WriteLine(reader["text"].ToString().TrimEnd());\n                    }\n                }\n            }\n\n            connection.Close();\n        }\n    }\n}