protected void GridView1_RowDataBound(object sender, GridViewRowEventArgs e)\n{\n    if (e.Row.RowType == DataControlRowType.DataRow)\n    {\n        // Assuming you want to change the value of the first column\n        DataRowView row = (DataRowView)e.Row.DataItem;\n        string originalValue = row["YourColumnName"].ToString();\n\n        // Modify the value as needed\n        string modifiedValue = "Modified: " + originalValue;\n\n        e.Row.Cells[0].Text = modifiedValue; // Change 0 to the index of your column\n    }\n}