using System;\nusing System.IO;\nusing System.Net;\nusing System.Data.SqlClient;\n\nclass DataImporter\n{\n    static void Main(string[] args)\n    {\n        try\n        {\n            // FTP download logic here\n            string ftpUrl = "ftp://ftp.example.com/file.txt";\n            string localFilePath = @"C:\path\to\local\file.txt";\n            string username = "username";\n            string password = "password";\n\n            using (WebClient request = new WebClient())\n            {\n                request.Credentials = new NetworkCredential(username, password);\n                byte[] ftpBytes = request.DownloadData(ftpUrl);\n                File.WriteAllBytes(localFilePath, ftpBytes);\n            }\n\n            // Process the downloaded file and insert data into SQL Server\n            string connectionString = "your connection string here";\n            using (SqlConnection conn = new SqlConnection(connectionString))\n            {\n                conn.Open();\n                using (StreamReader reader = new StreamReader(localFilePath))\n                {\n                    while (!reader.EndOfStream)\n                    {\n                        var line = reader.ReadLine();\n                        // Process the data from each line as needed\n                        // Here is a placeholder for processing logic and SQL command execution.\n                        string sql = "INSERT INTO YourTable (YourColumn) VALUES (@Value)";\n                        using (SqlCommand cmd = new SqlCommand(sql, conn))\n                        {\n                            // Add parameters with values after processing your line\n                            cmd.Parameters.AddWithValue("@value", line);\n                            cmd.ExecuteNonQuery();\n                        }\n                    }\n                }\n            }\n\n        }\n        catch (Exception ex)\n        {\n            // Log errors to a file or event viewer\n            Console.WriteLine("Error: " + ex.Message);\n        }\n    }\n}