using System;\nusing System.Text.RegularExpressions;\n\nclass Program\n{\n    static void Main()\n    {\n        string input = "Statements1  start Statements2 ; Statements3 end fun;";\n        // This should not match: string input = "Statements1   start Statements2 end Statements3 ; end fun";\n        \n        string pattern = @"(\b.*?\b)\s+(start) (\b.*?;.*?end\b)";\n        Regex regex = new Regex(pattern, RegexOptions.Singleline);\n\n        Match match = regex.Match(input);\n        if (match.Success)\n        {\n            Console.WriteLine("Match found:");\n            Console.WriteLine("1. Statements before 'start': " + match.Groups[1].Value.Trim());\n            Console.WriteLine("2. Keyword: " + match.Groups[2].Value);\n            Console.WriteLine("3. Statements after 'start' up to and including the first 'end': " + match.Groups[3].Value.Trim());\n        }\n        else\n        {\n            Console.WriteLine("No match found.");\n        }\n    }\n}